          #(neq, ":ai_bhvr", ai_bhvr_avoid_party), - Kham Removed (Nov 2018)
          (party_quick_attach_to_current_battle, ":party_no", ":enemy_side"), #attach as enemy
          (str_store_party_name, s1, ":party_no"),
